require 'benchmark_driver'
Benchmark.driver do |x|
  x.prelude %{
    WORD = 'sugoku-nagai-length-thousands-'
    def slow
      WORD.gsub('-', ' ')
    end

    def fast
      WORD.tr('-', ' ')
    end
  }
  x.report 'String#gsub', %{ slow }
  x.report 'String#tr', %{ fast }
end

__END__

